import React, { useState, useEffect } from 'react'
import { Nav, Navbar, NavDropdown, Container } from 'react-bootstrap';
import './topBar.css';

export const TopBar = () => {
  const api = {
    key: "e973687684a32f5b2f4bccca7c925f51",
    base: "https://api.openweathermap.org/data/2.5/",
  };

  const [weather, setWeather] = useState({});

  useEffect(() => {
    fetch(`${api.base}weather?q=Allentown&units=imperial&APPID=${api.key}`)
      .then((res) => res.json())
      .then((data) => {
        setWeather(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  useEffect(() => {
    console.log(weather);
  });


  return (
    <div>
      {typeof weather.main != "undefined" ? (
        <Navbar className="navigation" expand="lg">
          <Container>
            <Navbar.Brand>Lehigh Valley Tourism Bureau</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="me-auto">
                <NavDropdown
                  title={
                    <img src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}.png`}></img>
                  }
                  id="basic-nav-dropdown"
                >
                  <NavDropdown.Item>
                    Current Temperature: {Math.round(weather.main.temp)} &deg;F
                  </NavDropdown.Item>
                  <NavDropdown.Item>
                    Feels Like: {Math.round(weather.main.feels_like)} &deg;F
                  </NavDropdown.Item>
                  <NavDropdown.Item>
                    Current Conditions:{" "}
                    {weather.weather[0].description.charAt(0).toUpperCase() +
                      weather.weather[0].description.slice(1)}
                  </NavDropdown.Item>
                  <NavDropdown.Item>
                    Wind Speed: {weather.wind.speed} mph
                  </NavDropdown.Item>
                  <NavDropdown.Item>
                    Pressure: {weather.main.pressure} hPa
                  </NavDropdown.Item>
                </NavDropdown>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>
      ) : (
        <div></div>
      )}
    </div>
  );
};


export default TopBar;
